package com.java.j001;

public class Right_Angled {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n=4;
        for(int i=0;i<n;i++) {
        	for(int j=0;j<n-i;j++) {
        		System.out.print(j+"");
        	}
        	System.out.println();
        }
	}

}
